#include <vector>
#include <limits>
#include <iostream>

int uniquePathsWithObstacles(std::vector<std::vector<int>> &obstacleGrid) {
    int m = obstacleGrid.size(), n = obstacleGrid[0].size();
    std::vector<std::vector<long long>> table(m + 1, std::vector<long long>(n + 1));
    table[m][n - 1] = 1;

    for (int i = m - 1; i > -1; i--) {
        for (int j = n - 1; j > -1; j--) {
            table[i][j] = obstacleGrid[i][j] == 1 ? 0 : table[i + 1][j] + table[i][j + 1];
        }
    }

    return table[0][0];
}

